import{_ as r,o as s,c as l,b as e,d as a,a as i,e as n,r as d}from"./app.ba2f4e9c.js";var o="/images/diy/teleskop-viertelwellen-vertical/action-shot-stadpark.jpeg",u="/images/diy/teleskop-viertelwellen-vertical/radials.jpg",h="/images/diy/teleskop-viertelwellen-vertical/radial-mount-closeup.jpg",c="/images/diy/teleskop-viertelwellen-vertical/collapsed.jpg",m="/images/diy/teleskop-viertelwellen-vertical/swr-15m-band-cropped.jpg",p="/images/diy/teleskop-viertelwellen-vertical/smith-15m-band-cropped.jpg";const g={},b=i('<h1 id="teleskop-1-4-lambda-vertikal" tabindex="-1"><a class="header-anchor" href="#teleskop-1-4-lambda-vertikal" aria-hidden="true">#</a> Teleskop 1/4 Lambda Vertikal</h1><p><img src="'+o+'" alt="In Aktion im Stadtpark"> Eine Portable Viertelwellen Vertikalantenne f\xFCr 10 - 20 Meter und kleines Geld - selbstgebaut.</p><h2 id="bauteile-bezugsquellen" tabindex="-1"><a class="header-anchor" href="#bauteile-bezugsquellen" aria-hidden="true">#</a> Bauteile / Bezugsquellen</h2><p>Die Antenne besteht im Wesentlichen aus einer 5,6 m langen Teleskop Antenne, welche mit einer Halterung und einem Dorn im Erdboden befestigt wird.</p><p>Problematisch an g\xFCnstigen Teleskopantennen aus Fernost ist, dass diese, im Gegensatz zu z.B. den Produkten von MFJ, ein M10 Gewinde haben und nicht das g\xE4ngigere 3/8&quot;.</p><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>Wer schon eine HF-P1 oder eine PAC-12 Antenne besitzt, kann diese prima mit dem unten angegeben langen Teleskop kombinieren.</p></div><p>Daher hier eine kleine Liste mit Bezugsquellen und Bauteilen.</p>',7),_=e("thead",null,[e("tr",null,[e("th",null,"Bauteil"),e("th",{style:{"text-align":"right"}},"ca. Preis Stand 08/2022")])],-1),f={href:"https://de.aliexpress.com/item/1005004139955542.html",target:"_blank",rel:"noopener noreferrer"},k=n("5,6m Teleskopantenne mit M10 Gewinde - BD7IBI"),v=e("td",{style:{"text-align":"right"}},"25,00 Euro",-1),w={href:"https://smile.amazon.de/Albrecht-Antennenhalterung-3-8-6197-Silber/dp/B002VASO5U/",target:"_blank",rel:"noopener noreferrer"},A=n("Halterung"),B=e("td",{style:{"text-align":"right"}},"10,00 Euro",-1),z={href:"https://difona.de/amateurfunk/geraetezubehoer/ersatzteile/1880/einschraub-pl-auf-m-10-fuer-hfp-1",target:"_blank",rel:"noopener noreferrer"},x=n("Einbaufu\xDF M10 auf PL"),E=e("td",{style:{"text-align":"right"}},"8,50 Euro",-1),y={href:"https://www.ebay.de/itm/403789483361",target:"_blank",rel:"noopener noreferrer"},S=n("Litze f\xFCr Radiale - Fahrzeugleitung FLRY 0,75mm\xB2 o.\xE4"),V=e("td",{style:{"text-align":"right"}},"20,00 Euro",-1),R=e("tr",null,[e("td",null,"12mm durchmessender Aluminium Rundstab (Baumarkt, Amazon, ebay, Bastelkiste)"),e("td",{style:{"text-align":"right"}},"5,00 Euro")],-1),D=e("tr",null,[e("td",null,"Schrauben, Stecker ggf. Kabelschuhe (Baumarkt, Amazon, ebay, Bastelkiste)"),e("td",{style:{"text-align":"right"}},"10,00 Euro")],-1),F=e("tr",null,[e("td",null,[e("strong",null,"Summe")]),e("td",{style:{"text-align":"right"}},[e("strong",null,"78,50 Euro")])],-1),H=i('<div class="custom-container warning"><p class="custom-container-title">Hinweis</p><p>Die Teileauswahl ist nur ein Vorschlag und nicht notwendigerweise immer verf\xFCgbar. Wichtig ist es, das Prinzip der Bauweise zu verstehen und f\xFCr die Bauteile ggf. das zu benutzen, was man schon hat oder leicht bekommen/bauen kann. Wir freuen uns \xFCber Eure Abwandlungen.</p></div><h2 id="zusammenbau" tabindex="-1"><a class="header-anchor" href="#zusammenbau" aria-hidden="true">#</a> Zusammenbau</h2><p>Halterung und Einbaubuchse bed\xFCrfen im Aufbau keiner weiteren Erkl\xE4rung.</p><h3 id="erddorn" tabindex="-1"><a class="header-anchor" href="#erddorn" aria-hidden="true">#</a> Erddorn</h3><p>Von dem Aluminium Rundstab l\xE4ngt man ca. 30 cm ab.</p><p>Das Anspitzen erfolgt durch etwas manuelle Vorarbeit mit einer groben Pfeile und anschlie\xDFender Feinarbeit eingespannt in einer Bohrmaschine oder einen Akkuschrauber.</p><div class="custom-container danger"><p class="custom-container-title">Vorsicht</p><p>Schutzbrille aufsetzen und Handschuhe an! Metallsp\xE4ne im Auge oder sonst wo im K\xF6rper sind nicht cool \u{1F603}</p></div><h3 id="radials" tabindex="-1"><a class="header-anchor" href="#radials" aria-hidden="true">#</a> Radials</h3><p>Da es sich bei den Radials nicht um &quot;Elevated Radials&quot;, sondern &quot;Ground Radials&quot; handelt, m\xFCssen diese nicht abgestimmt sein. 16 bis 32 Radials in 2 - 3 m L\xE4nge sind ein guter Kompromiss in Hinblick auf Portabilit\xE4t und Antennen Fu\xDFabdruck.</p><p>In diesem Beispiel sind es 32 St\xFCck a 2,5 m L\xE4nge</p><p><img src="'+u+'" alt="Radilas"></p><p>Um die Radials an der Halterung zu befestigen, wurde ein weiteres Loch in die Halterung gebohrt und ein Gewinde geschnitten.</p><p>Nat\xFCrlich kann man die Radials auch direkt an den Schrauben der Halterung befestigen.</p><p><img src="'+h+'" alt="Radial Befestigung"></p><h3 id="fertig" tabindex="-1"><a class="header-anchor" href="#fertig" aria-hidden="true">#</a> Fertig</h3><p><img src="'+c+'" alt="Alles Fertig"></p><h2 id="abstimmung-aufbau" tabindex="-1"><a class="header-anchor" href="#abstimmung-aufbau" aria-hidden="true">#</a> Abstimmung / Aufbau</h2><p>Der Aufbau und die Abstimmung sind denkbar einfach:</p><ul><li>Alu Dorn in den Boden stecken</li><li>Radials Auslegen</li><li>Teleskopantenne aufschrauben</li><li>Mit VNA (z.B. Nano VNA) auf das gew\xFCnschte Band abstimmen.</li></ul><p>Noch schneller geht es, wenn man sich mit einem wasserfesten Stift kleine Markierungen an dem Teleskop macht, damit man die B\xE4nder schneller grob wiederfindet. Feinabgestimmt werden muss nat\xFCrlich trotzdem.</p><p>Die Antenne l\xE4sst sich durch Ausziehen des Teleskops problemlos f\xFCr 10 - 20 Meter abstimmen.</p><p>Hier ein paar Screenshots von den Abstimmwerten f\xFCr das 15 Meter Band , beim Aufbau im Stadtpark Hamburg.</p><p>Auf anderen B\xE4ndern sind die Ergebnisse \xE4hnlich.</p><p><strong>VSWR</strong>:</p><p><img src="'+m+'" alt="VSWR auf 15m"></p><p><strong>Smith Diagram</strong>:</p><p><img src="'+p+'" alt="Smith auf 15m"></p><h2 id="feedback" tabindex="-1"><a class="header-anchor" href="#feedback" aria-hidden="true">#</a> Feedback?</h2>',28),T=n("Du kannst mit uns auf "),P={href:"https://discord.gg/B6BkdcTQ87",target:"_blank",rel:"noopener noreferrer"},L=n("Discord"),M=n(" in Kontakt treten und uns mit Fragen bombardieren oder Feedback geben. Wie freuen uns.");function q(N,I){const t=d("ExternalLinkIcon");return s(),l("div",null,[b,e("table",null,[_,e("tbody",null,[e("tr",null,[e("td",null,[e("a",f,[k,a(t)])]),v]),e("tr",null,[e("td",null,[e("a",w,[A,a(t)])]),B]),e("tr",null,[e("td",null,[e("a",z,[x,a(t)])]),E]),e("tr",null,[e("td",null,[e("a",y,[S,a(t)])]),V]),R,D,F])]),H,e("p",null,[T,e("a",P,[L,a(t)]),M])])}var j=r(g,[["render",q],["__file","teleskop-viertelwellen-vertical.html.vue"]]);export{j as default};
